
public class SingleForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int input = 42;
        int sum = 0;
        
        for(int i = 1; i <= input; i++)
        {
          sum = sum + i;    // LINE A       
        }
        System.out.println();
        System.out.println("Sum of numbers till " + input + " is " + sum); // LINE B
    
    
		
		 	
	}
}


